import{_ as o,o as t,c as n,a as s}from"./app.17bdbb2b.js";const p={},r=s("<p>\u300A\u6700\u91CD\u8981\u7684\u4E8B\uFF0C\u53EA\u6709\u4E00\u4EF6\u300B\u8FD9\u672C\u4E66\u7684\u4F5C\u8005\uFF1A\u52A0\u91CC\u51EF\u52D2\u548C\u6770\u4F0A\u5E15\u5E15\u68EE\u3002</p><p>\u4F5C\u8005\u5728\u4E66\u91CC\u9762\u5F3A\u8C03\uFF0C\u6211\u4EEC\u5F53\u4E0B\u8981\u505A\u7684\u6700\u91CD\u8981\u7684\u4E8B\u60C5\u53EA\u6709\u4E00\u4EF6\u3002\u6211\u4EEC\u8981\u4E13\u6CE8\u4E8E\u4E00\u6B21\u53EA\u505A\u4E00\u4EF6\u4E8B\u60C5\uFF0C\u624D\u6709\u673A\u4F1A\u53D6\u5F97\u5353\u8D8A\u7684\u6210\u5C31\u3002</p><p>\u4F5C\u8005\u7684\u4E00\u4E2A\u6838\u5FC3\u89C2\u70B9\u662F\uFF0C\u627E\u5230\u4E00\u4E2A\u5173\u952E\u95EE\u9898\uFF0C\u89E3\u51B3\u8FD9\u4E2A\u5173\u952E\u95EE\u9898\uFF0C\u5C31\u80FD\u591F\u8BA9\u5176\u4ED6\u7684\u4E8B\u60C5\u53D8\u5F97\u4E0D\u518D\u91CD\u8981\u6216\u8005\u53D8\u5F97\u7B80\u5355\u3002\u6211\u4EEC\u53EA\u6709\u96C6\u4E2D\u7CBE\u529B\uFF0C\u4E13\u6CE8\u7684\u5904\u7406\u8FD9\u4E00\u7C7B\u7684\u4E8B\u60C5\uFF0C\u624D\u80FD\u591F\u50CF\u662F\u63A8\u5230\u591A\u7C73\u8BFA\u9AA8\u724C\u7684\u7B2C\u4E00\u5757\u724C\u4E00\u6837\uFF0C\u5E26\u6765\u5DE8\u5927\u7684\u8FDE\u9501\u53CD\u5E94\u3002</p><p>\u5728\u5F00\u59CB\u6240\u6709\u7684\u8BBA\u8FF0\u4E4B\u524D\uFF0C\u4F5C\u8005\u5411\u6211\u4EEC\u6F84\u6E05\u4E86<strong>\u51E0\u4E2A\u5E38\u89C1\u7684\u8BEF\u533A</strong>\u3002</p><p>\u6BD4\u5982\u6709\u4EBA\u8BA4\u4E3A\u6BCF\u4E00\u4EF6\u4E8B\u60C5\u90FD\u5F88\u91CD\u8981\uFF0C\u4F5C\u8005\u8BA4\u4E3A\u6309\u7167\u5E15\u7D2F\u6258\u539F\u5219\uFF0820/80\u539F\u5219\uFF09\uFF0C80%\u7684\u4E8B\u60C5\u53EA\u9700\u8981\u505A20%\u7684\u52AA\u529B\u5C31\u53EF\u4EE5\u51B3\u5B9A\u3002\u6240\u4EE5\u5E76<strong>\u4E0D\u662F\u6BCF\u4E00\u4EF6\u4E8B\u60C5\u90FD\u662F\u5E73\u7B49\u7684</strong>\u3002</p><p>\u6BD4\u5982\u6709\u4EBA\u8BA4\u4E3A\u4ED6\u4EEC\u53EF\u4EE5\u540C\u65F6\u5904\u7406\u591A\u4EF6\u4E8B\u60C5\u3002\u4F5C\u8005\u544A\u8BC9\u6211\u4EEC\uFF0C\u5176\u5B9E\u4EBA\u8111\u5904\u7406\u591A\u4EF6\u4E8B\u60C5\u548Ccpu\u5904\u7406\u591A\u4EF6\u4E8B\u60C5\u662F\u76F8\u4F3C\u7684\uFF0C\u53EA\u4E0D\u8FC7\u5148\u540E\u4F9D\u6B21\u5904\u7406\u4E86\u51E0\u4EF6\u4E8B\u60C5\uFF0C\u53EA\u4E0D\u8FC7cpu\u5904\u7406\u7684\u901F\u5EA6\u5F88\u5FEB\uFF0C\u6240\u4EE5\u6709\u4E86\u201C\u591A\u4EFB\u52A1\u5904\u7406\u201D\u7684\u8BF4\u6CD5\uFF0C\u4E0D\u8FC7\u662F\u4E00\u4E2A\u8BEF\u5BFC\u4EBA\u7684\u8BF4\u6CD5\u800C\u5DF2\u3002\u4EBA\u4EEC\u5728\u5207\u6362\u591A\u4E2A\u4EFB\u52A1\u4E2D\u95F4\uFF0C\u4F1A\u6709\u4E00\u90E8\u5206\u7684\u6548\u7387\u635F\u5931\uFF0C\u8FD9\u4E2A\u635F\u5931\u7387\u8FBE\u523025%~100%,\u8FD9\u662F\u5F88\u591A\u4EBA\u6CA1\u6709\u6CE8\u610F\u5230\u7684\u3002\u6240\u4EE5<strong>\u5F53\u4F60\u4E0D\u80FD\u4E13\u6CE8\u5904\u7406\u4E00\u4EF6\u4E8B\u60C5\uFF0C\u5728\u5F88\u591A\u4EF6\u4E8B\u60C5\u4E4B\u95F4\u6765\u56DE\u5207\u6362\u7684\u8BDD\uFF0C\u662F\u5F88\u8017\u8D39\u65F6\u95F4\u6548\u7387\u7684</strong>\u3002</p><p>\u6BD4\u5982\u6709\u4EBA\u8BA4\u4E3A\u8FC7\u4E0A\u6709\u89C4\u5F8B\u7684\u751F\u6D3B\uFF0C\u624D\u80FD\u63D0\u9AD8\u6548\u7387\u3002\u4F5C\u8005\u544A\u8BC9\u6211\u4EEC\uFF0C\u8FD9\u53C8\u662F\u4E00\u4E2A\u9A97\u5C40\u3002<strong>\u6211\u4EEC\u9700\u8981\u7684\u4E0D\u662F\u575A\u6301\uFF0C\u800C\u662F\u4E60\u60EF</strong>\u3002\u8981\u517B\u6210\u4E60\u60EF\uFF0C\u624D\u9700\u8981\u81EA\u5F8B\u3002\u6240\u4EE5\u6211\u4EEC\u53EA\u9700\u8981\u6311\u9009\u6700\u6709\u7528\u7684\u90A3\u4EF6\u4E8B\u60C5\u8FDB\u884C\u81EA\u5F8B\uFF0C\u575A\u6301\u4E0B\u53BB\u5C31\u53EF\u4EE5\u4E86\u3002\u4E0D\u9700\u8981\u4E8B\u4E8B\u505A\u5230\u81EA\u5F8B\u3002</p><p>\u518D\u6BD4\u5982\u6709\u4EBA\u8BA4\u4E3A\u53EF\u4EE5\u5E73\u8861\u5DE5\u4F5C\u548C\u751F\u6D3B\u3002\u4F5C\u8005\u544A\u8BC9\u6211\u4EEC\uFF0C\u5982\u679C\u4E00\u5B9A\u8981\u505A\u5230\u5E73\u8861\uFF0C\u90A3\u5C31\u4F1A\u5728\u4EFB\u4F55\u4E8B\u60C5\u4E0A\u90FD\u8868\u73B0\u5E73\u5E73\u3002\u6211\u4EEC\u60F3\u8981\u8FFD\u6C42\u5353\u8D8A\u5C31\u4E0D\u53EF\u80FD\u4E00\u76F4\u8FFD\u6C42\u5E73\u8861\u3002\u6211\u4EEC\u5BB3\u6015\u7684\u5176\u5B9E\u662F\u5931\u8861\uFF0C\u4F46\u4F5C\u8005\u8BA4\u4E3A\u6211\u4EEC\u4E0D\u5E94\u8BE5\u5BB3\u6015\u5931\u8861\uFF0C\u6240\u4EE5\u63D0\u51FA\u4E86\u201C<strong>\u5236\u8861</strong>\u201D\u7684\u6982\u5FF5\u3002\u5C31\u662F\u8BF4<strong>\u5931\u8861\u5230\u4E86\u4E00\u5B9A\u7A0B\u5EA6\u65F6\uFF0C\u8FDB\u884C\u5236\u8861\uFF0C\u4EE5\u4FDD\u8BC1\u751F\u6D3B\u6216\u8005\u5DE5\u4F5C\u671D\u7740\u6211\u4EEC\u60F3\u8981\u7684\u65B9\u5411\u524D\u8FDB</strong>\u3002\u540C\u65F6\u63D0\u51FA\u4E86\uFF0C<strong>\u751F\u6D3B\u9700\u8981\u77ED\u7EBF\u5236\u8861\uFF0C\u800C\u5DE5\u4F5C\u4E8B\u4E1A\u9700\u8981\u957F\u7EBF\u5236\u8861</strong>\u3002</p><p>\u8FD8\u6709\u53E6\u5916\u51E0\u4E2A\u8BEF\u533A\uFF0C\u5305\u62EC\u610F\u5FD7\u529B\u968F\u624B\u53EF\u5F97\uFF0C\u4EE5\u53CA\u5927\u5373\u4E0D\u4F73\u7B49\u3002\u8FD9\u91CC\u4E0D\u518D\u8D58\u8FF0\u3002</p><p>\u57FA\u4E8E\u4E0A\u8FF0\u7684\u5185\u5BB9\u3002\u4F5C\u8005\u8FDB\u4E00\u6B65\u63D0\u51FA\u4E86\u300A\u6700\u91CD\u8981\u7684\u4E8B\u60C5\u53EA\u6709\u4E00\u4EF6\u300B\u7684\u5173\u952E\u95EE\u9898\u662F\u4EC0\u4E48\u3002\u5173\u952E\u95EE\u9898\u4EE3\u8868\u4E86\u4E8B\u7269\u53D1\u5C55\u7684\u65B9\u5411\u3002\u627E\u5BF9\u4E86\u5173\u952E\u95EE\u9898\uFF0C\u624D\u80FD\u66F4\u597D\u7684\u56DE\u7B54\u95EE\u9898\uFF0C\u638C\u63E1\u95EE\u9898\u56DE\u7B54\u7684\u65B9\u5411\u3002\u5173\u952E\u95EE\u9898\u4E00\u822C\u5305\u62EC\u4E24\u4E2A\u65B9\u9762\uFF1A\u5927\u5C40\u548C\u7126\u70B9\u3002**\u201C\u6211\u80FD\u505A\u7684\u6700\u91CD\u8981\u7684\u4E8B\u60C5\u662F\u4EC0\u4E48\uFF1F\u4E3A\u4F55\u505A\u4E86\u8FD9\u4EF6\u4E8B\u60C5\u5C31\u4F1A\u8BA9\u5176\u4ED6\u7684\u4E8B\u53D8\u5F97\u66F4\u7B80\u5355\u6216\u8005\u4E0D\u5FC5\u8981\u4E86\u5462\uFF1F\u201D\u2014\u2014\u8FD9\u5C31\u662F\u672C\u6587\u7684\u5173\u952E\u95EE\u9898\u3002\u6211\u4EEC\u5C31\u662F\u8981\u56DE\u7B54\u8FD9\u6837\u7684\u4E00\u4E2A\u95EE\u9898\u3002**\u5927\u5C40\u7684\u95EE\u9898\u662F\uFF1A\u201C\u6211\u8981\u505A\u7684\u90A3\u4EF6\u6700\u91CD\u8981\u7684\u4E8B\u662F\u4EC0\u4E48\uFF1F\u201D\uFF0C\u7126\u70B9\u95EE\u9898\u662F\uFF1A \u201C\u6211\u73B0\u5728\u8981\u505A\u7684\u90A3\u4EF6\u6700\u91CD\u8981\u7684\u4E8B\u662F\u4EC0\u4E48\u201D\u3002</p><p>\u90A3\u4E48\u5982\u4F55\u628A\u8FD9\u4E2A\u4E8B\u60C5\uFF0C\u53D8\u6210\u81EA\u5DF1\u7684\u4E00\u4E2A\u4E60\u60EF\u5462\uFF1F\u4F5C\u8005\u4E5F\u7ED9\u51FA\u4E86\u4E00\u4E9B\u65B9\u6CD5\uFF1A\u8981\u7406\u89E3\u5E76\u575A\u4FE1\u4F60\u8981\u505A\u7684\u90A3\u4EF6\u6700\u91CD\u8981\u7684\u4E8B\uFF0C\u628A\u90A3\u4EF6\u6700\u91CD\u8981\u7684\u4E8B\u60C5\u5229\u7528\u8D77\u6765\uFF08\u4E5F\u5C31\u662F\u8BF4\uFF0C\u505A\u4E86\u8FD9\u4EF6\u4E8B\u60C5\uFF0C\u53EF\u4EE5\u8BA9\u5176\u4ED6\u4E8B\u60C5\u53D8\u5F97\u66F4\u52A0\u7B80\u5355\u6216\u8005\u4E0D\u518D\u5FC5\u8981\u4E86\uFF09\uFF0C\u6BCF\u5929\u95EE\u81EA\u5DF1\u201C\u8981\u505A\u54EA\u4EF6\u6700\u91CD\u8981\u7684\u4E8B\u201D\uFF0C\u4E0D\u65AD\u7684\u63D0\u9192\u81EA\u5DF1\uFF0C\u4EE5\u53CA\u5BFB\u6C42\u5E2E\u52A9\u3002</p><p>\u90A3\u4E48\uFF0C\u600E\u4E48\u7EE7\u7EED\u524D\u8FDB\u5462\uFF1F\u5982\u4F55\u627E\u5230\u524D\u9762\u95EE\u9898\u7684\u7B54\u6848\u5462\uFF1F\u4F5C\u8005\u7ED9\u51FA\u7684\u7B54\u6848\u5F88\u7B80\u5355\uFF1A<strong>\u63D0\u51FA\u4E00\u4E2A\u597D\u95EE\u9898\uFF0C\u7136\u540E\u60F3\u529E\u6CD5\u627E\u5230\u6B63\u786E\u7B54\u6848</strong>\u3002 \u4E00\u4E2A\u597D\u7684\u95EE\u9898\u5FC5\u987B\u662F<strong>\u5927\u800C\u5177\u4F53</strong>\u7684\u3002\u7B54\u6848\u5462\u53EF\u4EE5\u5206\u4E3A<strong>\u53EF\u884C\u6027\u7B54\u6848\u3001\u5EF6\u4F38\u7B54\u6848\u548C\u53EF\u80FD\u6027\u7B54\u6848</strong>\u3002\u6B63\u786E\u7684\u7B54\u6848\u53EF\u4EE5\u9020\u5C31\u975E\u51E1\u7684\u6210\u5C31\u3002 \u4F5C\u8005\u544A\u8BC9\u6211\u4EEC\uFF0C\u57FA\u4E8E\u5176\u4ED6\u4EBA\u7684\u7814\u7A76\u548C\u7ECF\u9A8C\u662F\u5BFB\u627E\u7B54\u6848\u7684\u6700\u4F73\u51FA\u53D1\u70B9\u3002\u6240\u4EE5\u6211\u4EEC\u8981\u8BBE\u7ACB\u57FA\u51C6\uFF0C\u8DDF\u968F\u8D8B\u52BF\uFF0C\u627E\u5230\u6700\u4F73\u7B54\u6848\u3002</p><p>\u672C\u4E66\u7684\u7B2C\u4E09\u90E8\u5206\uFF0C\u4F5C\u8005\u4ECB\u7ECD\u4E86\u5982\u4F55\u6210\u5C31\u5353\u8D8A\uFF0C\u91CA\u653E\u4F60\u7684\u5185\u5728\u6F5C\u529B\u3002\u4F5C\u8005\u76F4\u63A5\u7528\u4E09\u4E2A\u8BCD\u6982\u62EC\uFF1A<strong>\u76EE\u6807\u3001\u4F18\u5148\u4E8B\u7269\u4EE5\u53CA\u751F\u4EA7\u529B</strong>\u3002\u4E00\u4EF6\u5927\u4E8B\uFF1A\u4F60\u7684\u76EE\u6807\uFF1B\u4E00\u4EF6\u5C0F\u4E8B\uFF1A\u884C\u52A8\u8FC7\u7A0B\u4E2D\u505A\u4E8B\u7684\u8F7B\u91CD\u7F13\u6025\u3002\u4F5C\u8005\u9700\u8981\u6211\u4EEC\u627E\u5230\u81EA\u5DF1\u76EE\u6807\uFF0C\u8FDB\u800C\u5BF9\u8981\u505A\u7684\u4E8B\u60C5\u533A\u5206\u51FA\u4F18\u5148\u7EA7\u3002\u7136\u540E\u624D\u80FD\u53D1\u6325\u51FA\u5DE8\u5927\u7684\u751F\u4EA7\u529B\u3002 \u533A\u5206\u4E8B\u7269\u4F18\u5148\u7EA7\u8FC7\u7A0B\u4E2D\uFF0C\u8981\u77E5\u9053\uFF0C\u6700\u4F18\u5148\u7684\u4E8B\u60C5\u53EA\u6709\u4E00\u4EF6\uFF0C\u4E5F\u5C31\u662F\u6700\u91CD\u8981\u7684\u4E8B\u60C5\u3002\u901A\u8FC7\u5012\u63A8\u6CD5\u8BBE\u7ACB\u76EE\u6807\uFF0C\u5148\u627E\u5230\u4F60\u7684\u957F\u671F\u76EE\u6807\uFF0C\u7136\u540E\u4F30\u7B97\u5B9E\u73B0\u76EE\u6807\u9700\u8981\u7684\u6B65\u9AA4\uFF0C\u8003\u8651\u6BCF\u4E00\u6B65\u7684\u4F18\u5148\u4E8B\u52A1\uFF0C\u7531\u672A\u6765\u63A8\u5BFC\u81F3\u5F53\u4E0B\u3002\u91CD\u8981\u7684\u8FD8\u6709\u4E2A\u4E8B\u60C5\u5C31\u662F\u5199\u4E0B\u6765\u3002\u4E4B\u540E\u5C31\u53EA\u5269\u4E0B\u884C\u52A8\u4E86\u3002</p><p>\u884C\u52A8\u8FC7\u7A0B\u4E2D\uFF0C\u4F5C\u8005\u4ECB\u7ECD\u4E86\u4E00\u4E9B\u53EF\u884C\u7684\u65B9\u6CD5\uFF0C\u6BD4\u5982\u9884\u7559\u65F6\u95F4\u6BB5\u7684\u65B9\u5F0F\uFF1A\u4F9D\u6B21<strong>\u9884\u7559\u51FA\u7A7A\u95F2\u65F6\u95F4\u3001\u9884\u7559\u505A\u4F18\u5148\u4E8B\u7269\u7684\u65F6\u95F4\u3001\u4EE5\u53CA\u7559\u51FA\u505A\u8BA1\u5212\u7684\u65F6\u95F4</strong>\uFF0C\u540C\u65F6\u8FD8\u9700\u8981<strong>\u7EF4\u62A4\u4F60\u7684\u65F6\u95F4\u6BB5</strong>\u3002\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\u8FD8\u8981\u7ED9\u81EA\u5DF1\u4E09\u4E2A\u627F\u8BFA\u3002\u7B2C\u4E00\u4E2A\u662F\u5FC5\u987B\u8981\u5C06\u81EA\u5DF1\u505A\u7684\u4E8B\u60C5\uFF0C\u505A\u5230\u7CBE\u901A\u3002\u7B2C\u4E8C\u662F\u8981\u4E0D\u65AD\u53BB\u5BFB\u627E\u6700\u4F73\u89E3\u51B3\u65B9\u6848\u3002\u7B2C\u4E09\u4E2A\u662F\u4E3B\u52A8\u53CD\u601D\u81EA\u5DF1\u4E3A\u5B8C\u6210\u6700\u91CD\u8981\u7684\u4E00\u4EF6\u4E8B\u60C5\u6240\u505A\u7684\u4E00\u5207\u3002 \u7B80\u5316\u6765\u8BF4\uFF1A</p><p><strong>1\u3001\u8FBE\u5230\u7CBE\u901A\uFF1B2\u3001\u4ECE\u201CE\u201D\u5230\u201CP\u201D\uFF1B3\u3001\u9075\u5FAA\u95EE\u8D23\u5468\u671F\uFF08\u5BF9\u81EA\u5DF1\u8D1F\u8D23\uFF0C\u627E\u5230\u4E00\u4F4D\u4EBA\u751F\u5BFC\u5E08\uFF09</strong>\u3002</p><p>\u540C\u65F6\u4F5C\u8005\u4E5F\u63CF\u8FF0\u4E86\u56DB\u4E2A\u635F\u8017\u4F60\u7684\u6548\u7387\u7684\u95EE\u9898\uFF1A</p><p><strong>1\u3001\u4E0D\u4F1A\u8BF4\u201C\u4E0D\u201D\uFF1B2\u3001\u5BB3\u6015\u6DF7\u8BBA\uFF1B3\u3001\u7CDF\u7CD5\u7684\u5065\u5EB7\u4E60\u60EF\uFF1B4\u3001\u9006\u5883</strong></p><p>\u8FD9\u51E0\u4E2A\u95EE\u9898\u662F\u9700\u8981\u7279\u522B\u6CE8\u610F\u7684\uFF0C\u5426\u5219\u4F1A\u5077\u8D70\u4F60\u7684\u5927\u91CF\u7684\u65F6\u95F4\uFF0C\u8BA9\u4F60\u4E0D\u80FD\u4E13\u6CE8\u4E8E\u505A\u597D\u201C\u6700\u91CD\u8981\u7684\u90A3\u4E00\u4EF6\u4E8B\u201D\u3002</p>",18),g=[r];function e(c,i){return t(),n("div",null,g)}const _=o(p,[["render",e],["__file","ZuiZhongYaoDeShiZhiYouYiJian.html.vue"]]);export{_ as default};
